 * 数字转千分位金额
 * @param prefix 前缀，如¥
 * @param digit 小数位数，默认不处理，设置后根据值处理小数位数
 * @param emptyText
 * @returns {string}
 */
String.prototype.toMoney = function (prefix = '', digit, emptyText = '') {
  let val = this;
  if (val === '' || isNaN(val)) {
    return emptyText;
  }

  const re = /^(-?\d+)(\d{3})(\.?\d*)/;
  if (digit) {
    val = (val * 1).toFixed(digit);
  }
  while (re.test(val)) {
    val = val.replace(re, '$1,$2$3');
  }

  return prefix + val;
};

