function checkPrice (number) {
    let curVal = number;
    if (String(curVal) === "") {
        return true;
    }
    // 检验最基本的数字格式
    if (!(/^[-]?[\.\d]*$/u).test(curVal)) {
        return false;
    }
    // 校验正号
    if ((/[-]+/gu).test(curVal)) {
        return false;
    }
    // 首先保证只有一个小数点
    if (curVal.indexOf(".") !== curVal.lastIndexOf(".")) {
        return false;
    }
    let valArray = curVal.split(".");
    // 超过小数位长度
    if (valArray.length > 1 && valArray[1].length > 2) {
        return false;
    }
    // 超过整数位长度（过滤掉可能存在的-号）
    if (valArray[0].replace("-", "").length > 12) {
        return false;
    }
    return curVal;

