const setCookie = (name, value, time) => {
    // 当设置的时间等于0时，不设置expires属性，cookie在浏览器关闭后删除
    if(time !== 0){
        var exp = new Date();
        exp.setTime(exp.getTime() + time);
        document.cookie = name + "="+ escape(value) + ";expires=" + exp.toGMTString();
    }else{
        document.cookie = name + "=" + escape(value);
    }
};

